from . import uploader


def test_simple():
    data = uploader.image(
        'data:image/jpeg;base64,'
    )

    assert data[:3] == '000'
    assert data[-5:] == '.jpeg'

def test_png():
    data = uploader.image(
        'data:image/png;base64,'
    )

    assert data[:3] == '000'
    assert data[-4:] == '.png'
